            <script>
                const footer__steps_one = document.querySelector('.footer__steps--first'),
                    footer__steps_two = document.querySelector('.footer__steps--second'),
                    footer__steps_three = document.querySelector('.footer__steps--third'),
                    footer__steps_observer = new IntersectionObserver(entries => {
                        setTimeout(() => {
                            footer__steps_three.classList.toggle( 'footer__steps__animation', entries[0].isIntersecting );
                        }, "500");
                        setTimeout(() => {
                            footer__steps_two.classList.toggle( 'footer__steps__animation', entries[0].isIntersecting );
                        }, "1000");
                        setTimeout(() => {
                            footer__steps_one.classList.toggle( 'footer__steps__animation', entries[0].isIntersecting );
                        }, "1500");
                    });
                footer__steps_observer.observe( footer__steps_one, footer__steps_two, footer__steps_three );
            </script>
