<script>
    const
        preloaderMain = document.querySelector('.preloader__block'),
        mainLogo = document.querySelector('.prealoder__logo'),
        mainText = document.querySelector('.prealoder__text'),
        stepOne = document.querySelector('.preloader__steps--one'),
        stepTwo = document.querySelector('.preloader__steps--two'),
        stepThree = document.querySelector('.preloader__steps--three');
    if (localStorage.getItem("preloaderShown") === null){
        preloaderMain.style.display = 'block';
        setTimeout(() => {
            mainText.classList.add('step__animation');
        }, "200");
        setTimeout(() => {
            stepOne.classList.add('step__animation');
        }, "500");
        setTimeout(() => {
            stepTwo.classList.add('step__animation');
        }, "1000");
        setTimeout(() => {
            stepThree.classList.add('step__animation');
        }, "1500");
        setTimeout(() => {
            mainLogo.classList.add('step__animation');
        }, "2000");
        setTimeout(() => {
            stepOne.classList.remove('step__animation');
            stepTwo.classList.remove('step__animation');
            stepThree.classList.remove('step__animation');
        }, "2500");
        setTimeout(() => {
            preloaderMain.classList.add('preloader__animation');
        }, "4000");
        setTimeout(() => {
            preloaderMain.remove();
        }, "5000");
        localStorage.setItem("preloaderShown", "true");
    } else {
        preloaderMain.remove();
    }
</script>
